import os
import requests

def download_image(url, file_path):
    response = requests.get(url)
    if response.status_code == 200:
        with open(file_path, 'wb') as file:
            file.write(response.content)
        print(f"Успешно загружено изображение: {file_path}")
    else:
        raise Exception(f"Ошибка при загрузке изображения: {response.status_code} - {response.text}")

def download_posters(movie_ids, posters_urls, images_folder):
    if not os.path.exists(images_folder):
        os.makedirs(images_folder)
        
    for movie_id, poster_url in zip(movie_ids, posters_urls):
        try:
            if poster_url:
                poster_filename = f"{movie_id}.jpg"
                poster_filepath = os.path.join(images_folder, poster_filename)
                download_image(poster_url, poster_filepath)
            else:
                print(f"Постер для фильма с id {movie_id} не найден.")
        except Exception as e:
            print(f"Ошибка при обработке фильма с id {movie_id}: {e}")

